package org.dita.dost.util;

import org.dita.dost.TestUtils;
import org.junit.AfterClass;
import org.junit.BeforeClass;
import org.junit.Test;

import java.io.File;
import java.io.IOException;

import static org.junit.Assert.assertEquals;

public class TestImgUtils {

    private static final File resourceDir = TestUtils.getResourceDir(TestImgUtils.class);
    private static final File srcDir = new File(resourceDir, "src");
    private static File tempDir;

    @BeforeClass
    public static void setUp() throws IOException {
        tempDir = TestUtils.createTempDir(TestImgUtils.class);
        final Job job = new Job(tempDir);
        job.write();
    }

    @Test
    public void testgetwidth() {
        assertEquals(135, ImgUtils.getWidth(srcDir.getAbsolutePath(), "img.jpg"));
    }

    @Test
    public void testgetheight() {
        assertEquals(95, ImgUtils.getHeight(srcDir.getAbsolutePath(), "img.jpg"));
    }

    @Test
    public void testgetbindata() {
        assertEquals("",
                ImgUtils.getBinData(srcDir.getAbsolutePath(), "img.jpg"));
    }

    @AfterClass
    public static void tearDown() throws IOException {
        TestUtils.forceDelete(tempDir);
    }

}
